enum UrlencodedError {
    Chunked,
    Overflow {
            size: usize,
            limit: usize,
        },
    UnknownLength,
    ContentType,
    Parse(FormDeError),
    Encoding,
    Serialize(FormError),
    Payload(PayloadError),
}

enum JsonPayloadError {
    OverflowKnownLength {
            length: usize,
            limit: usize,
        },
    Overflow {
            limit: usize,
        },
    ContentType,
    Deserialize(JsonError),
    Serialize(JsonError),
    Payload(PayloadError),
}

enum ReadlinesError {
    EncodingError,
    Payload(PayloadError),
    LimitOverflow,
    ContentTypeError(ContentTypeError),
}

enum ContentRangeSpec {
    Bytes {
            range: Option<(u64, u64)>,
            instance_length: Option<u64>,
        },
    Unregistered {
            unit: String,
            resp: String,
        },
}

enum ConditionMiddlewareFuture<E, D> {
    Enabled {
            fut: E,
        },
    Disabled {
            fut: D,
        },
}

fn header<K, V>(Self, K, V) -> Self where HeaderName: TryFrom<K>,
<HeaderName as TryFrom<K>>::Error: Into<HttpError>, HeaderValue: TryFrom<V>,
<HeaderValue as TryFrom<V>>::Error: Into<HttpError>

enum ErrorHandlersFuture<Fut, B> where Fut: Future {
    ServiceFuture {
            fut: Fut,
            handlers: Handlers<B>,
        },
    ErrorHandlerFuture {
            fut: LocalBoxFuture,
        },
}

fn log_target<impl Into<Cow<'static, str>>>(Self,
impl Into<Cow<'static, str>>) -> Self where
impl Into<Cow<'static, str>>: Into<Cow>

fn with_header<impl TryIntoHeaderPair>(Self, impl TryIntoHeaderPair)
-> CustomizeResponder<Self> where Self: Sized,
impl TryIntoHeaderPair: TryIntoHeaderPair

fn url_for<U, I>(&Self, &HttpRequest, &str, U)
-> Result<Url, UrlGenerationError> where U: IntoIterator<Item = I>,
I: AsRef<str>

fn service<S, E>(Self, S) -> Self where S: ServiceFactory<ServiceRequest,
Response = ServiceResponse, Error = ServiceResponse, InitError =
ServiceResponse, Config = ServiceResponse> + 'static, E: Into<Error> + 'static

enum JsonBody<T> {
    Error(Option<JsonPayloadError>),
    Body {
            limit: usize,
            length: Option<usize>,
            payload: Decompress<Payload>,
            buf: BytesMut,
            _res: PhantomData<T>,
        },
}
